import java.util.*;import java.awt.*;import javax.swing.*;public class GraphButton extends JFrame{    public static Button North;    public static Button East;    public static Button West;    public static Button South;    public static JButton Center;    public static void main(String[] args)    {        JFrame a = new JFrame();        a.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        North = new Button("N");        East = new Button("E");        South = new Button("S");        West = new Button("W");        Center = new JButton("C");        a.add(North, BorderLayout.NORTH);        a.add(East, BorderLayout.EAST);        a.add(South, BorderLayout.SOUTH);        a.add(West, BorderLayout.WEST);        a.add(Center);        randomButton();        a.setSize(400, 400);        a.setVisible(true);        System.out.println("Successful run. ");    }    public static void randomButton()    {        Random rand = new Random();        int num = rand.nextInt(5);        if (num == 0)        {            randomColor(North);        } else if (num == 1)        {            randomColor(East);        } else if (num == 2)        {            randomColor(South);        } else if (num == 3)        {            randomColor(West);        }    }    public static Button randomColor(Button randButton)    {        Random rand = new Random();        int num = rand.nextInt(7);        if (num == 0)        {            randButton.setBackground(Color.blue);        } else if (num == 1)        {            randButton.setBackground(Color.black);        } else if (num == 2)        {            randButton.setBackground(Color.green);        } else if (num == 3)        {            randButton.setBackground(Color.yellow);        } else if (num == 4)        {            randButton.setBackground(Color.red);        } else if (num == 5)        {            randButton.setBackground(Color.white);        }        return randButton;    }}